script:
frappe.call({
    method: "frappe.client.get_list",
    args: {
        doctype: "Project",
        fields: [ "name", "project_name", "percent_complete", "custom_project_status", "modified"], 
        order_by: "modified desc",
        limit: 5
    },
    callback: function(response) {
        if (response.message) {
            var projects = response.message.slice(0, 10);
            var table = document.createElement('table');
            table.classList.add('project-table');

            // Create table header
            var headerRow = table.insertRow();
            var headers = ["Project Name",  "Status", "Progress", "Last Updated"];
            headers.forEach(function(header) {
                var th = document.createElement('th');
                th.textContent = header;
                headerRow.appendChild(th);
            });

            // Populate table with project details
            projects.forEach(function(project) {
                var row = table.insertRow();

                var projectNameCell = row.insertCell();
                projectNameCell.textContent = project.project_name;
                projectNameCell.classList.add('project-name');

                var statusCell = row.insertCell();
                var statusParagraph = document.createElement('p');
                statusParagraph.textContent = project.custom_project_status;


                 if (project.custom_project_status === "Delayed") {
                    statusParagraph.classList.add('delayed-status');
                } else if (project.custom_project_status === "In Progress") {
                    statusParagraph.classList.add('in-progress-status');
                } else if (project.custom_project_status === "Completed") {
                    statusParagraph.classList.add('completed-status');
                } else if (project.custom_project_status === "Open") {
                    statusParagraph.classList.add('open-status');
                } else if (project.custom_project_status === "Ongoing") {
                    statusParagraph.classList.add('ongoing-status');
                } else if (project.custom_project_status === "New") {
                    statusParagraph.classList.add('new-status');
                }

                statusCell.appendChild(statusParagraph);

                var progressCell = row.insertCell();
                // Create custom progress bar
                var progressBar = document.createElement('div');
                progressBar.classList.add('progress-bar');
                progressBar.style.width = '100px';

                var progressValue = document.createElement('div');
                progressValue.classList.add('progress-value');
                progressValue.style.width = project.percent_complete + '%';

                progressBar.appendChild(progressValue);
                progressCell.appendChild(progressBar);

                var lastUpdatedCell = row.insertCell();
                lastUpdatedCell.textContent = formatRelativeTime(project.modified);

                row.addEventListener('click', function() {
                    window.location.href = '/app/project/' + project.name;
                });
            });

            // Append table to HTML element
            var container = root_element.querySelector('#projectList');
            container.innerHTML = ''; // Clear previous content
            container.appendChild(table);
        }
    }
});


function formatRelativeTime(modifiedDate) {
    var now = new Date();
    var modified = new Date(modifiedDate);
    var diff = now - modified;
    var seconds = Math.floor(diff / 1000);
    var minutes = Math.floor(seconds / 60);
    var hours = Math.floor(minutes / 60);
    var days = Math.floor(hours / 24);
    var weeks = Math.floor(days / 7);
    var months = Math.floor(days / 30);
    var years = Math.floor(days / 365);

    if (years > 0) {
        return years + (years === 1 ? " year ago" : " years ago");
    } else if (months > 0) {
        return months + (months === 1 ? " month ago" : " months ago");
    } else if (weeks > 0) {
        return weeks + (weeks === 1 ? " week ago" : " weeks ago");
    } else if (days > 0) {
        return days + (days === 1 ? " day ago" : " days ago");
    } else if (hours > 0) {
        return hours + (hours === 1 ? " hour ago" : " hours ago");
    } else if (minutes > 0) {
        return minutes + (minutes === 1 ? " minute ago" : " minutes ago");
    } else {
        return "Just now";
    }
}

